INSERT INTO [dbo].[Info] ([name], [type], [information], [title], [webadd], [news], [newsadd], [tsum]) VALUES (N'Alphabet', N'（其他）', N'Alphabet是重组后的“伞形公司”（Umbrella Company）名字，采取结构，把旗下搜索、、其它网络与研发投资部门分离开来。 ;Alphabet将取代 INC成为，所有谷歌的都会自动转换为相同数量的Alphabet股票，并享有同等利。;新谷歌将成为Alphabet的，将分别是GOOGL和GOOG。;Alphabet 现任 CEO Larry Page 正在悄悄开发飞行汽车，其以个人名义投资了两家研发 “飞行汽车” 的创业公司。 ;2018年2月7日，Nest重新纳入谷歌旗下。 ;', N'Alphabet因隐瞒Google+隐私漏洞被投资者起诉', N'https://www.itjie.com/46036.html', N'无', N'无', N'2')
