INSERT INTO [dbo].[Info] ([name], [type], [information], [title], [webadd], [news], [newsadd], [tsum]) VALUES (N'机器人', N'（人工智能）', N'人（Robot）是自动执行工作的机器。它既可以接受人类指挥，又可以运行预先编排的程序，也可以根据以技术制定的原则行动。它的任务是协助或取代人类工作的工作，例如生产业、建筑业，或是危险的工作。;', N'菜鸟未来园区首次投入天猫双11， IoT和机器人唱主角', N'https://www.itjie.com/46852.html', N'无', N'无', N'20')
