INSERT INTO [dbo].[Info] ([name], [type], [information], [title], [webadd], [news], [newsadd], [tsum]) VALUES (N'智能手机', N'（人工智能）', N'智能手机，是指像一样，具有的，独立的运行空间，可以由用户自行、、等第三方服务商提供的，并可以通过移动通讯网络来实现无线网络接入手机类型的总称。;智能手机具有优秀的操作系统、可自由安装各类软件（仅安卓系统）、完全大屏的全式操作感这三大特性，其中Google()、 、、、（宏达电） 这五大品牌在全世界最广为皆知，而（Mi）、（HUAWEI）、(MEIZU)、（Lenovo）、（ZTE）、（Coolpad）、（oneplus）、（GIONEE）、天宇（，K-Touch）等品牌在中国备受关注。;', N'英媒：研究发现美国人平均每天看手机52次', N'https://www.itjie.com/48676.html', N'2018，中国手机绝地求生', N'https://baike.baidu.com/tashuo/browse/content?id=570fd4bbf71e9d8aebe3fc54&lemmaId=94396&lemmaId=94396&fr=qingtian', N'13')
