INSERT INTO [dbo].[Info] ([name], [type], [information], [title], [webadd], [news], [newsadd], [tsum]) VALUES (N'机器学习', N'（大数据）', N'机器学习(Machine Learning, ML)是一门多领域交叉学科，涉及概率论、统计学、、、理论等多门学科。专门研究计算机怎样模拟或实现人类的学习行为，以获取新的知识或技能，重新组织已有的知识结构使之不断改善自身的性能。;它是的核心，是使计算机具有智能的根本途径，其应用遍及人工智能的各个领域，它主要使用归纳、综合而不是演绎。;', N'', N'', N'无', N'无', N'1')
