INSERT INTO [dbo].[Info] ([name], [type], [information], [title], [webadd], [news], [newsadd], [tsum]) VALUES (N'车联网', N'（物联网）', N'根据中国物联网校企联盟的定义，车联网（Internet of Vehicles）是由车辆位置、速度和路线等信息构成的巨大交互网络。通过GPS、RFID、传感器、摄像头图像处理等装置，车辆可以完成自身环境和状态信息的采集；通过互联网技术，所有的车辆可以将自身的各种信息传输汇聚到中央处理器；通过计算机技术，这些大量车辆的信息可以被分析和处理，从而计算出不同车辆的最佳路线、及时汇报路况和安排信号灯周期。', N'小鹏汽车“双十一”首战收官　收获订单146例', N'http://www.gd.xinhuanet.com/newscenter/2018-11/13/c_1123702580.htm', N'无', N'无', N'38')
