INSERT INTO [dbo].[Info] ([name], [type], [information], [title], [webadd], [news], [newsadd], [tsum]) VALUES (N'智能网联汽车', N'（物联网）', N'智能网联汽车，即ICV（全称Intelligent Connected Vehicle），是指车联网与智能车的有机联合，是搭载先进的车载传感器、控制器、执行器等装置，并融合现代通信与网络技术，实现车与人、车、路、后台等智能信息交换共享，实现安全、舒适、节能、高效行驶，并最终可替代人来操作的新一代汽车。', N'江阴公安交警大队走进广电中心体验融媒体魅力', N'http://jy.js.xinhuanet.com/2018-12/20/c_1123880904.htm', N'无', N'无', N'30')
