INSERT INTO [dbo].[Info] ([name], [type], [information], [title], [webadd], [news], [newsadd], [tsum]) VALUES (N'小鹏汽车', N'（物联网）', N'小鹏汽车成立于2014年，总部位于广州，是广州橙行智动汽车科技有限公司旗下的互联网电动汽车品牌，由何小鹏、夏珩、何涛等人发起，团队主要成员来自广汽、福特、宝马、特斯拉、德尔福、法雷奥等知名整车与大型零部件公司，以及阿里巴巴、腾讯、小米、三星、华为等知名互联网科技企业。', N'除了玩游戏，VR已经能做这么多事情了', N'http://www.ah.xinhuanet.com/2018-04/11/c_1122659807.htm', N'无', N'无', N'10')
