INSERT INTO [dbo].[Info] ([name], [type], [information], [title], [webadd], [news], [newsadd], [tsum]) VALUES (N'大客户营销体验中心', N'（物联网）', N'大客户营销体验中心（Marketing Experience Center Of The Key Account）；“大客户”是企业的伙伴型客户，是企业忠实的客户，是为企业创造80%利润的客户，是为企业带来高收益而企业只需支付低服务成本的客户，是能够将企业引向你期望的方向的客户，因为他们与企业建立的是长期的可盈利关系。这部分客户为企业节省了开发新顾客的成本、为企业带来了长期利润，并且帮助企业诱发潜在顾客。', N'深圳南山：VR体验“毒”后感', N'http://www.nncc626.com/2018-12/11/c_1210013344.htm', N'无', N'无', N'44')
