INSERT INTO [dbo].[Info] ([name], [type], [information], [title], [webadd], [news], [newsadd], [tsum]) VALUES (N'阿里巴巴集团', N'（大数据）', N'是以曾担任英语教师的为首的18人于1999年在创立的公司。 ;阿里巴巴集团经营多项业务，另外也从关联公司的业务和服务中取得经营商业生态系统上的支援。业务和关联公司的业务包括：、、、、阿里巴巴国际交易市场、、、、、等。 ;2014年9月19日，阿里巴巴集团在正式挂牌上市，股票代码“BABA”，创始人和董事局主席为。;2018年7月19日，全球同步《财富》世界500强排行榜发布，阿里巴巴集团排名300位。 2018年12月，阿里巴巴入围2018世界品牌500强。 ;', N'阿里巴巴下跌：周二盘中创出146.47美元52周新低', N'https://www.itjie.com/45230.html', N'阿里巴巴：马云从未转让和退出淘宝的股份', N'http://news.sina.com.cn/s/2019-01-07/doc-ihqhqcis3875368.shtml', N'13')
