INSERT INTO [dbo].[Info] ([name], [type], [information], [title], [webadd], [news], [newsadd], [tsum]) VALUES (N'AFs', N'（大数据）', N'AFS国际文化交流组织（AFS Intercultural Programs）是一个从事国际间教育交流的非盈利性民间国际组织。该组织成立于1947年，前身是(American Field Service)，成立于1914年，其成员都来自于中的战地救护人员，创始人是皮埃特·安德鲁（Piatt Andrew）。;可供出售就金融资产。;', N'FW出局LMS网友怒喷：没有Uzi的命得了Uzi的病', N'https://www.itjie.com/45798.html', N'无', N'无', N'1')
