INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'FF称恒大不遵守公平协议，欢迎新投资人一起实现梦想', N'10月8日下午，法拉第发布公告称恒大未能兑现向FF支付任何额外资金的承诺，反而试图获得对FF中国和FF所有IP的控制权及所有权。而就在昨晚中国恒大发布公告称贾跃亭要踢恒大出局，至此，法拉第和恒大正式开撕。', N'https://www.itjie.com/44716.html')
