INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'万国邮联回应：对美国退出联盟事件表示遗憾', N'美国已通知万国邮政联盟（万国邮联）其决定退出万国邮联的条约。对此，万国邮联总干事Bishar A.Hussein表示遗憾，称将寻求与美国政府代表会晤，以进一步讨论此事。', N'https://www.itjie.com/46136.html')
