INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'高通拟在美国境外建造最大园区，投资4亿美元', N'上周六高通公司高管会见了印度特仑甘纳邦IT部长KT Rama Rao，探讨了该公司在海德拉巴市的发展计划，该项目的第一期工程将包括建造170万平方英尺的办公大楼，可以容纳1万名员工，该公司将会在推动印度无线革命中扮演重要角色，努力让印度移动通讯变得更便宜和更易使用。', N'https://www.itjie.com/44710.html')
