INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'摩拜单车App内上线了实物电商业务“摩拜商城”', N'近日，摩拜单车App内上线了实物电商业务“摩拜商城”。雄墨商业了解到，商城内销售的产品主要为骑行周边产品，同时还涉及服装、家居、电子、护肤等类目，用户可以通过App内的虚拟货币“魔币”兑换打折券或者用现金购买产品，有赞为其提供技术支持。', N'https://www.itjie.com/44907.html')
GO
