INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'腾讯QQ不再提供单独“帐号申诉”入口', N'11月26日讯，腾讯近日发布《帐号改密、找密、更换密保手机服务升级及申诉服务下线的公告》，称由于安全功能升级，目前帐号找回/修改密码/更换密保手机全面升级使用“资料身份验证”功能，验证成功后即可修改QQ密码和重置密保工具，故将不再提供单独“帐号申诉”入口，如需要找回/修改密码、更换密保手机，可登录QQ安全中心官网修改。', N'https://www.itjie.com/49559.html')
GO
