INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'Boring公司首条隧道打通，马斯克发推祝贺', N'埃隆·马斯克(Elon Musk)旗下的钻探公司Boring本周达到了一个新的里程碑：其位于洛杉矶的首条隧道已经打通。马斯克在自己的推特账户上发布了一段钻探机械打通隧道的视频，展示了这台机器到达终点的过程。', N'https://www.itjie.com/48740.html')
