INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'联想常程：当前5G是噱头，预计2020年才会普及', N'常程表示，当前所谓的5G都是噱头，因为从产品端来说不可能准备好，所以更多的是大家在炒作一些能力，我有这样的能力。所以从产品上来说，大家更多的是在做技术的储备。5G真的能带来变化，我个人判断是在2020年，2019年还谈不上5G对产业有什么变化，可能有一些产品出来，更多的可能是大家在准备2020年的5G会有比较大的转折。', N'https://www.itjie.com/46151.html')
