INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'马云：脱贫是给人以希望，不仅是给人以钱财', N'马云认为，“联产责任承包制”解决了土地上种出来的东西属于谁的问题，大力激发了农民生产积极性，而今天大数据、互联网要解决土地上种出来的东西卖给谁的问题。', N'https://www.itjie.com/44145.html')
