INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'国家计算机病毒应急处理中心： 9 款App存在违法行为', N'据新华社报道，近日，国家计算机病毒应急处理中心通过互联网监测发现，九款违法有害移动应用存在于移动应用发布平台中，其主要危害涉及隐私窃取、资费消耗和流氓行为三类。 这些违法有害移动应用包括：《土豪漫画》《米奇速借》《搞笑手机铃声》《3D手机铃声大全》《失恋回避》《创造男友汉化版》《魔王多米诺的推倒法汉化版》《给我离婚汉化版》《我的花心女友》。', N'https://www.itjie.com/50801.html')
