INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'百度起诉搜狗浏览器劫持流量：申请法院下达行为保全禁令', N'北京市海淀区人民法院针对百度起诉搜狗浏览器恶意劫持百度hao123流量、经百度多次要求但仍拒不停止劫持行为的情况，对搜狗公司下达行为保全禁令，勒令其立即停相关劫持行为。', N'https://www.itjie.com/44704.html')
