INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'哔哩哔哩宣布收购网易漫画资产', N'昨日晚间，哔哩哔哩宣布已与网易签署收购协议，将对旗下网易漫画的主要资产进行收购，其中包括APP、网站、部分漫画版权及其相关使用权益。 在此次收购正式宣布前，B站已于 11 月 13 日上线了自己的独立APP“哔哩哔哩漫画”，内容包括《航海王》、《银魂》、《精灵宝可梦》、《JOJO的奇妙冒险》等日漫及多部人气国漫。', N'https://www.itjie.com/51120.html')
