INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'苹果AR挡风玻璃系统细节：全新UI交互界面，支持FaceTime通话', N'苹果申请了有关汽车的多项专利，最新的一项专利申请显示，苹果正在开发AR挡风玻璃系统。值得一提的是，这项专利技术允许乘客通过该AR显示系统接打FaceTime通话。专利还显示，通过AR技术，透明的挡风玻璃能够感知周围的真实环境，并向车内成员发送包含天气、路况、车速、路径导航等丰富信息。', N'https://www.itjie.com/45091.html')
GO
