INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'中国移动香港宣布将与华为联合建设面向5G的综合承载网', N'从华为获悉，近日，中国移动香港有限公司(CMHK)宣布将与华为联合建设面向5G的综合承载网。该网络将采用华为5G综合承载解决方案，涵盖5G承载、企业专线、家宽等三大业务场景。5G综合承载网的建设标志着CMHK从纯移动运营商向FMC(固定移动融合)综合业务运营商的战略转型，也是双方在承载网建设上的一次全新合作。', N'https://www.itjie.com/44959.html')
