INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'谷歌给AI上“紧箍咒”：创建公司AI原则，让AI减少偏见遵守道德标准', N'在去年谷歌会议上，该公司当时的人工智能(AI)主管表示，我们不需要担心杀手机器人，相反，我们需要担心偏见。今年6月，谷歌首席执行官桑达尔?皮查伊发布了公司的人工智能原则，用于指导谷歌涵盖这些领域的工作和研究，包括确保谷歌员工不会制造或强化不公平的偏见。', N'https://www.itjie.com/47925.html')
