INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'抖音购物车功能正式开放申请', N'昨日，抖音官方宣布，抖音购物车功能正式开放申请，平台上发布视频大于 10 个且达到 8000 以上粉丝的实名认证账号可自助申请。 发布视频大于 10 个且达到 8000 以上粉丝的实名认证账号可通过抖音App——我的——右上角···——商品分享功能自助申请开通，通过审核即可使用。', N'https://www.itjie.com/50996.html')
