INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'YouTube用户观看游戏内容时长一年超过500亿小时', N'12月10日讯，根据VentureBeat报道，每天有超过2亿人在YouTube观看游戏内容，在过去一年里，YouTube游戏视频观看时长总长超过500亿小时。接下来，YouTube Gaming将与游戏出版商合作，并继续寻找创新方案，开发货币化产品，帮助创作者用更多方法赚更多钱。', N'https://www.itjie.com/50843.html')
GO
