INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'SAP宣布将以80亿美元收购调查软件公司Qualtrics', N'德国商业软件巨头SAP宣布将以80亿美元收购总部位于美国的调查软件公司Qualtrics。收购完成后，该公司将成为SAP云业务的一部分，但保留其位于犹他州和西雅图的两个总部，以及自己的品牌和员工。', N'https://www.itjie.com/47919.html')
