INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'WeWork从软银获得30亿美元新增投资', N'11月14日消息，路透社的一份投资者报告显示，美国共享办公空间提供商WeWork Cos已从日本软银集团公司获得30亿美元的新增投资。 软银将在1月15日向WeWork支付15亿美元，其余的将在4月15日支付。 此前7月，WeWork表示已从投资者那里筹集了5亿美元，其中包括淡马锡控股和软银集团公司。', N'https://www.itjie.com/48049.html')
