INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'腾讯上线音乐短视频应用音兔', N'10月17日讯，腾讯近日上线音乐短视频应用音兔（Intoo）。通过音兔 app，用户可以将视频与图片素材快速制作为音乐短视频。音兔有视频一键剪辑、智能照片整理、智能匹配转场特效、智能音乐匹配四大核心功能。该应用目前仅有 iOS 版本，Android 版本仍未上线。', N'https://www.itjie.com/45948.html')
