INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'离开谷歌回归斯坦福，“AI女神”李飞飞新动向揭晓', N'李飞飞宣布斯坦福大学启动“以人为本人工智能项目”，目标是创建“以人为本人工智能研究院”，李飞飞与斯坦福大学前副校长，哲学家John Etchemendy共同担任HAI研究院院长。', N'https://www.itjie.com/46326.html')
