INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'阿里妈妈或将推出全新的信息流营销产品', N'记者近日获悉，阿里集团旗下的数字营销中台阿里妈妈即将推出全新的信息流营销产品，这一产品将成为阿里妈妈成立十余年来，首次出现的与直通车、钻展平行的拳头业务产品。这一产品将提供丰富的信息展现形式，包括商品、图文、视频、清单、权益等淘系内的原生内容，并确保能够完整覆盖消费者购物的全链路。', N'https://www.itjie.com/46042.html')
