INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'阿里云双11拼团一折活动最后一天，速度上车了', N'阿里云双11拼团一折活动最后一天，速度上车了，云服务器1折，最低99元一年。 云服务器1核2G1M1年99元、2年199元、3年298元；2核4G1M1年545元、2年927元、3年1227元；2核8G5M3年2070元，4核8G5M3年5370元，8核16G10M3年16170元，1核1G数据库1年119元，堪称是阿里云双11全年最低价，手快有，手慢无，错过就要再等一年！ ? 地址：立即参加 ?', N'https://www.itjie.com/47812.html')
