INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'腾讯正在申请商标“企鹅物流”', N'12月14日讯，引述新浪科技消息，据中国商标总局官网信息显示，腾讯公司正在申请商标“企鹅物流”，目前申请状态为“等待实质审查”。 据商标总局信息显示，企鹅物流涉及的业务包括：运输信息、物流运输、运输预订、商品包装、商品打包、旅行预订、运输、货物贮存、快递服务、包裹投递。', N'https://www.itjie.com/51353.html')
