INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'《堡垒之夜》在iOS端的收入突破3亿美元', N'根据移动应用市场研究公司Sensor Tower的数据报告，在不到七个月的时间里，《堡垒之夜》在IOS端的收入达到了3亿美元，其中美国市场占据65%的收入来源。《堡垒之夜》的收入比Supercell在2016年3月推出的热门游戏《皇室战争》同期高出32%左右，是腾讯在中国市场推出的《王者荣耀》的两倍多。', N'https://www.itjie.com/44525.html')
