INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'马斯克回应欺诈指控：无正当理由，对此深感难过和失望', N'美国证券交易委员会周四向美国曼哈顿联邦法院对特斯拉CEO马斯克提起诉讼，指控其有意误导投资者，涉嫌欺诈，寻求禁止他在美国上市公司担任高管或董事。马斯克则回应称，对此深感难过和失望。', N'https://www.itjie.com/43949.html')
