INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'一加明年将发布欧洲第一款商用5G手机', N'2018年12月6日，一加手机CEO刘作虎受邀出席在美国夏威夷举办的第三届高通骁龙技术峰会，并作为特别嘉宾在会上发言。刘作虎在会上宣布与英国最大的移动运营商EE达成战略合作，一加明年将发布欧洲第一款商用5G手机。 ?', N'https://www.itjie.com/50633.html')
