INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'特斯拉计划打造超级充电站网络，马斯克称明年底数量会翻倍', N'为了缓解电动汽车车主的“充电苦恼”，马斯克近日在推文中写到：“到明年（2019）年底，特斯拉超充容量容量将会翻倍，预计可覆盖所有活跃市场的95%~100%用户”。', N'https://www.itjie.com/49432.html')
