INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'张朝阳请求股东批准解散搜狐集团控股美国母公司', N'今日张朝阳向搜狐股东发布公开信，请求批准解散搜狐特拉华公司。根据该提案，搜狐特拉华公司将被解散；搜狐特拉华公司已发行的所有普通股将被注销，代表搜狐开曼公司普通股的美国存托股份(“ADS”)将按比例分配给搜狐特拉华公司股东。', N'https://www.itjie.com/43958.html')
