INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'锤子科技法人变更：由罗永浩变为温洪喜', N'昨日，有消息称，北京锤子数码科技有限公司进行了法人变更。 据天眼查数据显示， 2018 年 12 月 5 日，北京锤子数码科技有限公司法定代表由罗永浩变更为温洪喜; 2018 年 12 月 5 日，该公司 9 名董事退出，其中包括钱晨、唐岩以及郑刚等，温洪喜为新增经理。 此外， 2018 年 11 月 28 日，锤子科技(北京)有限公司改为锤子科技(北京)股份有限公司。', N'https://www.itjie.com/50990.html')
GO
