INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'滴滴成立国庆长假应急工作指挥部，程维亲自任总指挥', N'滴滴发布《国庆长假出行应急保障通告及出行提示》，称滴滴已成立长假应急保障工作指挥部，由程维任总指挥、柳青任副总指挥，成员包括各事业部和各城市负责人。', N'https://www.itjie.com/44124.html')
