INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'美国计算机协会公布杰出科学家名单，达摩院数据库首席科学家李飞飞入选', N'据了解，李飞飞教授带领的团队所研发的新一代分布式数据库系统，支撑了阿里巴巴经济体的复杂业务、海量数据和双11交易洪峰的挑战，已经被应用于多个城市的智能城市交通网络管理。', N'https://www.itjie.com/47406.html')
