INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'京东物流正式推出个人快递业务，将在北上广开始运营', N'京东物流CEO王振辉在2018全球智能物流峰会上宣布，京东将正式进军个人快递业务。这项业务将在北上广地区开始运营。个人用户可从京东App、公众号、小程序等入口使用这项服务。', N'https://www.itjie.com/46121.html')
