INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'淘宝上线轻店铺：淘宝自己的小程序', N'淘宝轻店铺开始内测。尽管不叫小程序，长得也更像微信品牌页，但轻店铺身上还是有小程序的影子：便捷开发，有品牌信息展示外的群聊、发文章等功能，希望打通线上线下会员体系。这是既微信、支付宝、百度、今日头条之后，又一个想做小程序平台的超级 app。', N'https://www.itjie.com/44408.html')
