INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'三星将更换5G网络业务负责人', N'12月10日讯，据华尔街日报消息，知情人士透露，三星电子将更换其5G网络设备业务负责人，该部门目前正在快速扩大全球份额，三星的目标是两年内赢得全球20%的5G设备市场。 目前该业务负责人由Kim Young-ky担任。Kim Young-ky卸任后，将继续担任该公司顾问一职。三星将在下周公布年底人事变动时宣布该消息。', N'https://www.itjie.com/50807.html')
