INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'百度“青松计划”实施：为员工父母提供商业保险', N'百度今日通过内部邮件宣布“青松计划”正式开始实施，凡是司龄达到两年及以上员工的父母双方，都可在全国范围内享受住院医疗保障。', N'https://www.itjie.com/44713.html')
