INSERT INTO [dbo].[mInfo] ([title], [content], [webadd]) VALUES (N'苹果升级隐私保护，美国用户将可查看哪些数据被保存', N'苹果公司正在推进用户隐私升级，计划推出一个门户网站，允许客户搜索并查看该公司保留了那些类型的用户数据。苹果公司希望可以将自己定位为一间硬件公司，而非Facebook等利用用户数据进行营销的广告公司。', N'https://www.itjie.com/46090.html')
